import React, { useEffect, useState } from 'react';
import useGithub from '../../hooks/github-hooks';
import RepositoryItem from '../repository-item';
import * as S from "./styled";

const Repositories = () => {

  const {githubState, getUserRepo} = useGithub();
  const [hasUserForSearchrepos,  setHasUserForSearchrepos] = useState(false);

  useEffect( () => {
    if ( !!githubState.user.login ) {
      getUserRepo();
    }
    setHasUserForSearchrepos( !!githubState.repositories);
  }, [githubState.user]);

  return (
    <>
        {hasUserForSearchrepos ?  (
            <S.WrapperTabs selectedTabClassName="is-selected" selectedTabPanelClassName="is-selected">
                <S.WrapperTabList>
                    <S.WrapperTab> Repositories </S.WrapperTab>
                    <S.WrapperTab> Starred </S.WrapperTab>
                </S.WrapperTabList>
                <S.WrapperTabPanel> 
                    <RepositoryItem 
                        name="app-ideas" 
                        linkToRepo="https://github.com/benits/app-ideas" 
                        fullname="benits/app-ideas"/> 
                </S.WrapperTabPanel>
                <S.WrapperTabPanel> 
                    <RepositoryItem 
                          name="benits" 
                          linkToRepo="https://github.com/benits/benits" 
                          fullname="benits/benits"/>    
                </S.WrapperTabPanel>
            </S.WrapperTabs>
        ) : (
          <></>
        )}    
    </>    
  )
}

export default Repositories;
